mapper.select:
select
  order_id, payment, payment_type, post_fee, status, create_time, update_time, payment_time, consign_time, end_time, close_time, shipping_name, shipping_code, user_id, buyer_message, buyer_nick, buyer_rate, receiver_area_name, receiver_mobile, receiver_zip_code, receiver, expire, invoice_type, source_type, seller_id
from pinyougoudb.tb_order


        and order_id = #{orderId}


        and payment = #{payment}


        and payment_type = #{paymentType}


        and post_fee = #{postFee}


        and status = #{status}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and payment_time = #{paymentTime}


        and consign_time = #{consignTime}


        and end_time = #{endTime}


        and close_time = #{closeTime}


        and shipping_name = #{shippingName}


        and shipping_code = #{shippingCode}


        and user_id = #{userId}


        and buyer_message = #{buyerMessage}


        and buyer_nick = #{buyerNick}


        and buyer_rate = #{buyerRate}


        and receiver_area_name = #{receiverAreaName}


        and receiver_mobile = #{receiverMobile}


        and receiver_zip_code = #{receiverZipCode}


        and receiver = #{receiver}


        and expire = #{expire}


        and invoice_type = #{invoiceType}


        and source_type = #{sourceType}


        and seller_id = #{sellerId}
